contract Fund{
address amanger=msg.sender;
mapping(address=>uint) public money;
function sendMoney() public payable {
    
    require(msg.value>=1000000000000000000,"sorry increase amount");
   money[msg.sender]+=msg.value;



}
function dra(uint amount) external payable{
    require(money[msg.sender]>=amount,"sorry u not");
    msg.sender.transfer(amount);
money[msg.sender]-=amount;
}
}
